// n8n Expert AI Content Distribution with MCP Intelligence
const strategy = $node['🎯 n8n-Expert Content Strategy Agent'].json;
const mcpAnalysis = $node['🔧 MCP Workflow Validator & Builder'].json;
const content = $node['✅ n8n Expert Quality Validator'].json.validation_score >= 80 
  ? $node['✍️ n8n Expert Content Writer'].json.content 
  : $node['🔄 Content Improvement Loop'].json.content;

const distributionPlan = {
  content: content,
  strategy: strategy,
  mcp_analysis: mcpAnalysis,
  platforms: [],
  scheduled_times: [],
  adaptations: {},
  n8n_optimizations: []
};

// n8n-specific AI-driven platform selection
if (strategy.content_type === 'blog') {
  distributionPlan.platforms.push('website', 'linkedin', 'medium');
  distributionPlan.adaptations.linkedin = 'Professional summary with n8n workflow examples';
  distributionPlan.adaptations.medium = 'Thought leadership with n8n automation insights';
  distributionPlan.n8n_optimizations.push('Include n8n workflow diagrams', 'Add node configuration examples');
} else if (strategy.content_type === 'social') {
  distributionPlan.platforms.push('twitter', 'linkedin', 'facebook');
  distributionPlan.adaptations.twitter = 'Thread format with n8n tips';
  distributionPlan.adaptations.facebook = 'Community-focused n8n automation discussion';
  distributionPlan.n8n_optimizations.push('Include n8n node screenshots', 'Add workflow pattern explanations');
}

// n8n-intelligent scheduling based on audience analysis
const now = new Date();
distributionPlan.platforms.forEach((platform, index) => {
  const scheduleTime = new Date(now);
  scheduleTime.setHours(now.getHours() + (index * 2)); // Stagger releases for optimal n8n community engagement
  distributionPlan.scheduled_times.push(scheduleTime.toISOString());
});

// n8n Expert AI performance prediction
distributionPlan.predicted_engagement = {
  score: Math.min(95, strategy.engagement_potential || 75),
  factors: [
    'n8n Expert AI-optimized content',
    'Multi-agent n8n validation',
    'Platform-specific n8n adaptations',
    'Real MCP integration insights',
    'n8n workflow intelligence'
  ],
  n8n_expertise_boost: 15 // Additional engagement from n8n-specific knowledge
};

return [distributionPlan];

