// Advanced Keyword Research Data Aggregation
const strategy = $node['ğŸ  Real Estate Keyword Strategist'].json;
const keywordData = $node['ğŸ“Š DataForSEO Keyword Analyzer'].json;
const serpData = $node['ğŸ” SERP Analysis Engine'].json;
const intelligence = $node['ğŸ§  Keyword Intelligence Processor'].json;

const keywordResearchResults = {
  research_id: `keyword_research_${Date.now()}`,
  timestamp: new Date().toISOString(),
  location: $input.first().json.location || 'United States',
  research_scope: 'Real Estate - Comprehensive',

  // Strategic Framework
  keyword_strategy: strategy.keyword_strategy,
  research_priorities: strategy.research_priorities,

  // Keyword Categories
  property_keywords: {
    residential: strategy.property_type_keywords.filter(k => k.includes('home') || k.includes('house') || k.includes('condo')),
    commercial: strategy.property_type_keywords.filter(k => k.includes('commercial') || k.includes('office') || k.includes('retail')),
    luxury: strategy.property_type_keywords.filter(k => k.includes('luxury') || k.includes('estate') || k.includes('waterfront')),
    investment: strategy.property_type_keywords.filter(k => k.includes('investment') || k.includes('rental') || k.includes('flip'))
  },

  // Geographic Targeting
  geographic_keywords: {
    hyperlocal: strategy.geographic_keywords.filter(k => k.includes('neighborhood') || k.includes('zip')),
    city_level: strategy.geographic_keywords.filter(k => k.includes('city') || k.includes('downtown')),
    regional: strategy.geographic_keywords.filter(k => k.includes('county') || k.includes('metro'))
  },

  // Intent-Based Keywords
  buyer_intent: {
    informational: strategy.buyer_intent_keywords.filter(k => k.includes('how') || k.includes('what') || k.includes('guide')),
    transactional: strategy.buyer_intent_keywords.filter(k => k.includes('buy') || k.includes('agent') || k.includes('service')),
    commercial: strategy.buyer_intent_keywords.filter(k => k.includes('best') || k.includes('top') || k.includes('review'))
  },

  // DataForSEO Intelligence
  keyword_metrics: keywordData?.tasks?.[0]?.result?.map(keyword => ({
    keyword: keyword.keyword,
    search_volume: keyword.search_volume,
    competition: keyword.competition,
    cpc: keyword.cpc,
    difficulty: keyword.keyword_difficulty || 'N/A'
  })) || [],

  // SERP Analysis Results
  serp_analysis: {
    total_results: serpData?.tasks?.[0]?.result?.[0]?.items_count || 0,
    serp_features: serpData?.tasks?.[0]?.result?.[0]?.items?.map(item => item.type) || [],
    top_competitors: serpData?.tasks?.[0]?.result?.[0]?.items?.slice(0, 10)?.map(item => ({
      domain: item.domain,
      title: item.title,
      rank: item.rank_absolute
    })) || []
  },

  // Intelligence Insights
  opportunities: intelligence.keyword_opportunities || [],
  competitive_analysis: intelligence.competitive_analysis || {},
  content_recommendations: intelligence.content_recommendations || [],
  optimization_priorities: intelligence.optimization_priorities || [],

  // Performance Projections
  roi_projections: intelligence.roi_projections || {
    expected_traffic_increase: '150%+',
    ranking_improvement_timeline: '3-6 months',
    lead_generation_boost: '300%+',
    market_authority_score: 'High'
  },

  // Implementation Roadmap
  implementation_plan: intelligence.implementation_roadmap || [
    'Phase 1: High-priority keyword optimization',
    'Phase 2: Content creation and optimization',
    'Phase 3: Local SEO integration',
    'Phase 4: Performance monitoring and refinement'
  ],

  // Seasonal Strategy
  seasonal_opportunities: strategy.seasonal_opportunities || {
    spring: 'New listing season optimization',
    summer: 'Family home and relocation focus',
    fall: 'Investment property strategies',
    winter: 'Market preparation and planning'
  },

  // Success Metrics
  success_metrics: {
    total_keywords_analyzed: (keywordData?.tasks?.[0]?.result?.length || 0),
    high_opportunity_keywords: intelligence.keyword_opportunities?.length || 0,
    competitive_gaps_identified: Object.keys(intelligence.competitive_analysis || {}).length,
    content_opportunities: intelligence.content_recommendations?.length || 0
  }
};

return [keywordResearchResults];

